class MatirialPageStud:
    def openmainmenu(self):
        root.destroy()
        Studentmainmenu.vp_start_gui()

    def clickdownload(self, the_file, file_name):

        file = filedialog.asksaveasfile(initialdir='/', title="בחר שם ומיקום להורדה", mode='wb',
                                        filetypes=[('PDF files', '*pdf'), ('RAR files', '*.rar'),
                                                   ('ZIP files', '*zip')],
                                        defaultextension='.pdf', initialfile=file_name)
        if file==None:
            return
        file.write(base64.b64decode(the_file))

