class healthreport:
    def report(self):
        message = ""
        global Users
        for user in Users.find({'id': {'$exists': True}}):
            if 'health' in user:
                if user['health'] == str(date.today()):
                    message += str(user['id']) + '   ' + user['name'] +'\n'
        return message
